import React, { useState } from 'react';
import Navbar from '../../components/Navbar';
import { useNavigate } from 'react-router-dom';
import './login.css'; // Ensure this points to the correct CSS file

function Login() {
  const [usernameOrEmail, setUsernameOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();

    const loginData = {
      username_or_email: usernameOrEmail, // Can be username or email
      password: password,
    };

    try {
      const response = await fetch('/api/login/', {  // ✅ Ensure this URL is correct
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginData),
      });

      const data = await response.json();

      if (response.ok) {
        alert(`Login successful! Welcome, ${data.username}`);
        localStorage.setItem('token', data.token);  // ✅ Store token for authentication
        localStorage.setItem('user', JSON.stringify(data)); // ✅ Store user details
        navigate('/');  // ✅ Redirect to dashboard after login
      } else {
        setError(data.error || 'Invalid credentials or not approved by admin.');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('An error occurred. Please try again later.');
    }
  };

  return (
    <>
      <div>
        <Navbar />
      </div>
      <div className="login_container">
        <div className="login_image_container">
          <img src="/Images/side-image_(3).jpg" alt="Decorative" />
        </div>
        <div className="login_box">
          <div className="login_form">
            <div className="form_title">Login</div>
            {error && <div className="error_message">{error}</div>} {/* ✅ Show error messages */}
            <div className="input_elements">
              <div className="login_items">
                <img src="/Images/email_icon.png" alt="User Icon" />
                <input
                  type="text"
                  placeholder="Username or Email"
                  value={usernameOrEmail}
                  onChange={(e) => setUsernameOrEmail(e.target.value)}
                  required
                />
              </div>
              <div className="login_items">
                <img src="/Images/pwd_icon.png" alt="Password Icon" />
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
            </div>
            <div className="submit-container">
              <button className="submit" onClick={handleSubmit}>Login</button>
            </div>
            <div className="links">
              <a href="/signup">Don't have an account? Sign Up</a>
              <br />
              <a href="/forgot-password">Forgot Password?</a>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
