import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';  
import './Navbar.css';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const [userType, setUserType] = useState(null);

  useEffect(() => {
    // Retrieve user type from localStorage (or from API if needed)
    const storedUserType = localStorage.getItem('userType'); // e.g., 'retail_seller', 'staff', 'wholesale_dealer'
    setUserType(storedUserType);
  }, []);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark position-absolute w-100 fixed-top">
        <div className="container-fluid">
          <a className="navbar-brand" href="/">
            <img src="/Images/logo.png" alt="Logo" width="100" height="50" className="d-inline-block align-text-top" />
          </a>
          <button className="navbar-toggler" type="button" onClick={toggleSidebar}>
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end">
            <ul className="navbar-nav d-none d-lg-flex">
              <li className="nav-item">
                <Link className="nav-link" to="/">Home</Link> 
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/products">Products</Link> 
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/about_us">About Us</Link> 
              </li>
              <li className="nav-item">
                {userType ? (
                  <Link className="nav-link" to="/profile">Profile</Link> 
                ) : (
                  <Link className="nav-link" to="/signup">Login</Link> 
                )}
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <button className="close-btn" onClick={toggleSidebar}>&times;</button>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/products">Products</Link></li>
          <li><Link to="/about_us">About Us</Link></li>
          <li>
            {userType ? (
              <Link to="/profile">Profile</Link>
            ) : (
              <Link to="/signup">Login</Link>
            )}
          </li>
        </ul>
      </div>

      {/* Optional Backdrop */}
      {isOpen && <div className="backdrop" onClick={toggleSidebar}></div>}
    </>
  );
}

export default Navbar;
