import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './rt_cart.css';

function RtCart() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  // Debug: Log cart contents and localStorage
  useEffect(() => {
    console.log("Cart Contents:", cart);
    console.log("LocalStorage Cart:", localStorage.getItem('cart'));
  }, [cart]);

  // Initialize cart from localStorage on component mount
  useEffect(() => {
    try {
      const savedCart = localStorage.getItem('cart');
      console.log("Saved Cart from LocalStorage:", savedCart);
      
      if (savedCart) {
        const parsedCart = JSON.parse(savedCart);
        console.log("Parsed Cart:", parsedCart);
        
        // Validate the parsed cart
        if (Array.isArray(parsedCart)) {
          setCart(parsedCart);
        } else {
          console.error("Invalid cart data in localStorage");
          localStorage.removeItem('cart');
        }
      }
    } catch (error) {
      console.error("Error loading cart from localStorage:", error);
      localStorage.removeItem('cart');
    }
  }, []);

  // Calculate total price
  const calculateTotal = () => {
    return cart.reduce((total, item) => {
      // Ensure item has a price and quantity
      const itemPrice = item.displayPrice || item.retail_price || item.wholesale_price || item.normal_price;
      return total + (itemPrice * item.quantity);
    }, 0);
  };

  // Remove item from cart
  const removeItem = (productId) => {
    const updatedCart = cart.filter(item => item.id !== productId);
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  // Update cart item quantity
  const updateQuantity = (productId, newQuantity) => {
    const updatedCart = cart.map(item => 
      item.id === productId 
        ? {...item, quantity: Math.max(1, newQuantity)}
        : item
    ).filter(item => item.quantity > 0);
    
    setCart(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
  };

  // Proceed to checkout
  const proceedToCheckout = () => {
    navigate('/checkout', { state: { cartItems: cart } });
  };

  return (
    <div className="cart-container">
      <h1>Your Cart</h1>
      
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div className="cart-items">
          {cart.map((item) => (
            <div key={item.id} className="cart-item">
              <img 
                src={item.image || '/Images/placeholder.png'} 
                alt={item.name} 
                className="cart-item-image"
              />
              <div className="cart-item-details">
                <h2>{item.name}</h2>
                <p>Price: ₹{item.displayPrice || item.retail_price || item.wholesale_price || item.normal_price}</p>
                <div className="quantity-controls">
                  <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
                </div>
                <button onClick={() => removeItem(item.id)}>Remove</button>
              </div>
            </div>
          ))}
          
          <div className="cart-summary">
            <h3>Total: ₹{calculateTotal().toFixed(2)}</h3>
            <button onClick={proceedToCheckout}>Proceed to Checkout</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default RtCart;
