import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../../components/Navbar';
import './login.css'; 

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');

    try {
      const response = await fetch('/api/login/', {  
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username,
          password: password,
        }),
        credentials: 'include'
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Login failed');
      }

      // ✅ Store user details in localStorage (optional)
      localStorage.setItem('username', data.username);
      localStorage.setItem('role', data.role);

      // ✅ Redirect user based on role
      if (data.role === 'admin') {
        navigate('/admin-dashboard');  // ✅ Redirect Admin
      } else {
        navigate('/dashboard');  // ✅ Redirect User
      }
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <>
      <Navbar />
      <div className='login_container'>
        <div className='login_image_container'>
          <img src='/Images/side-image_(3).jpg' alt='Decorative' />
        </div>
        <div className='login_box'>
          <div className='login_form'>
            <div className='form_title'>Login</div>
            {error && <p className="error_message">{error}</p>} {/* ✅ Show errors */}
            <form onSubmit={handleSubmit}>
              <div className='input_elements'>
                <div className='login_items'>
                  <img src='/Images/user_icon.png' alt='User Icon' />
                  <input 
                    type='text' 
                    placeholder='Username' 
                    value={username} 
                    onChange={handleUsernameChange} 
                    required 
                  />
                </div>
                <div className='login_items'>
                  <img src='/Images/pwd_icon.png' alt='Password Icon' />
                  <input 
                    type='password' 
                    placeholder='Password' 
                    value={password} 
                    onChange={handlePasswordChange} 
                    required 
                  />
                </div>
              </div>
              <div className='submit-container'>
                <button type="submit" className='submit'>Login</button>
              </div>
            </form>
            <div className='links'>
              <a href='/signup'>Don't have an account? Sign Up</a>
              <br />
              <a href='/forgot-password'>Forgot Password?</a>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login;
