import React, { useState, useEffect, useContext } from 'react';
import { useLocation } from 'react-router-dom';
import './rtform.css'; // Import the CSS
import Navbar from '../../components/Navbar';
import { FormDataContext } from './FormDataContext';  // Import the context

function RtReg() {
  const location = useLocation();
  const { formData } = useContext(FormDataContext); // Use the context
  const [username, setUsername] = useState(formData.username || '');
  const [email, setEmail] = useState(formData.email || '');
  const [shopName, setShopName] = useState('');
  const [ownerName, setOwnerName] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [storeAddress, setStoreAddress] = useState('');

  // Function to fetch the user's location
  // const getLocation = () => {
  //   if (navigator.geolocation) {
  //     navigator.geolocation.getCurrentPosition(
  //       (position) => {
  //         const { latitude, longitude } = position.coords;
  //         // Generate Google Maps link
  //         const mapLink = `https://www.google.com/maps?q=${latitude},${longitude}`;
  //         setLocationLink(mapLink);
  //       },
  //       (error) => {
  //         console.error("Error getting location:", error);
  //         alert("Unable to retrieve your location.");
  //       }
  //     );
  //   } else {
  //     alert("Geolocation is not supported by your browser.");
  //   }
  // };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Prepare the data to send to the Django backend
    const formDataToSend = {
      username: username,
      email: email,
      shop_name: shopName,
      owner_name: ownerName,
      mobile_number: mobileNumber, 
      
    };

    try {
      const response = await fetch('/api/register-retailer/', {  // Replace with your Django endpoint
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // You might need to include a CSRF token here
        },
        body: JSON.stringify(formDataToSend),
      });

      if (response.ok) {
        alert('Registration successful!  Awaiting admin verification.');
        // Optionally, redirect the user to a "thank you" page or login page
      } else {
        const errorData = await response.json();
        console.error('Registration failed:', errorData);
        alert('Registration failed. Please try again.');
      }
    } catch (error) {
      console.error('Error during registration:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <>
      <div>
        <Navbar />
      </div>
      <div className='rtreg-signup_container'>
        <div className='rtreg-image_container'>
          <img src='\Images\side-image_(1).jpg' alt='Decorative' />
        </div>
        <div className='rtreg-signup_box'>
          <div className='rtreg-signup_form'>
            <div className='text'>Registration</div>
            <form onSubmit={handleSubmit}>
              <div className='rtreg-input_elements'>
                <div className='rtreg-input_items'>
                  <img src='\Images\user_icon.png' alt='User Icon' />
                  <input type='text' placeholder='Username' value={username} onChange={(e) => setUsername(e.target.value)} />
                </div>
                <div className='rtreg-input_items'>
                  <img src='\Images\email_icon.png' alt='Email Icon' />
                  <input type='email' placeholder='Email' value={email}  onChange={(e) => setEmail(e.target.value)}/>
                </div>
                <div className='rtreg-input_items'>
                  <img src='\Images\email_icon.png' alt='Email Icon' />
                  <input
                    type='text'
                    placeholder='Shop name'
                    value={shopName}
                    onChange={(e) => setShopName(e.target.value)}
                    required
                  />
                </div>
                <div className='rtreg-input_items'>
                  <img src='\Images\email_icon.png' alt='Email Icon' />
                  <input
                    type='text'
                    placeholder='Owner name'
                    value={ownerName}
                    onChange={(e) => setOwnerName(e.target.value)}
                    required
                  />
                </div>
                <div className='rtreg-input_items'>
                  <img src='\Images\email_icon.png' alt='Email Icon' />
                  <input
                    type='number'
                    placeholder='Mobile number'
                    value={mobileNumber}
                    onChange={(e) => setMobileNumber(e.target.value)}
                    required
                  />
                </div>
                
                <div className='rtreg-input_items' >
                <img src='\Images\email_icon.png' alt='Email Icon' />
                  <textarea type='text' placeholder='Store Address'
                    value={storeAddress}
                    onChange={(e) => setStoreAddress(e.target.value)}
                    required  
                    className="rtreg-address-input"
                  />
                </div>
              </div>
              <div className='submit-container'>
                <button type="submit" className='submit'>Register</button>
              </div>
            </form>
            <div className='links'>
              <a href='/login'>Already have an account? Login</a>
              <br />
              <a href='/forgot-password'>Forgot Password?</a>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default RtReg;
