import React, { useState } from 'react';
import './staff.css';
import Navbar from '../../components/Navbar';

function Staff() {
  const [isSidebarVisible, setSidebarVisible] = useState(false);

  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  return (
    <>
      <div><Navbar /></div>
      <div className='staff-container'>
        {/* Sidebar Button */}
        <button
          className='btn btn-primary sidebar-button'
          onClick={toggleSidebar}
        >
          {isSidebarVisible ? 'Hide Sidebar' : 'Show Sidebar'}
        </button>

        {/* Sidebar */}
        <div
          className={`sidebar ${isSidebarVisible ? 'visible' : ''}`}
        >
          <ul className="list-unstyled p-3">
            <li className="py-2">Attendance</li>
            <li className="py-2">Salary</li>
            <li className="py-2">Profile Details</li>
            <li className="py-2">Change Password</li>
            <li className="py-2">Logout</li>
          </ul>
        </div>

        {/* Main Content */}
        <div className='staff-profile'>
          <div className='staff-name'>Helloo</div>
        </div>
      </div>
    </>
  );
}

export default Staff;
