import React, { useState, useEffect } from "react";
import { useAuth } from "../../AuthenticationContext";
import "./products.css";
import Navbar from "../../components/Navbar";

function Products() {
  const [products, setProducts] = useState([]); 
  const [searchTerm, setSearchTerm] = useState(""); 
  const [cart, setCart] = useState([]); // New state to manage cart
  const { isAuthenticated, userType } = useAuth(); 
  
  // Fetch products from the Django backend
  useEffect(() => {
    fetch("/api/products/", {
      headers: {
        'Authorization': isAuthenticated ? `Token ${localStorage.getItem('authToken')}` : '',
      }
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        const productList = Array.isArray(data) ? data : (data.products || []);
        setProducts(productList.map(product => ({
          ...product,
          cartQuantity: 0 // Add cart quantity property
        })));
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, [isAuthenticated]);
  
  // Function to get the correct price based on user type
  const getDisplayPrice = (product) => {
    if (isAuthenticated && (userType === "retail_seller" || userType === "retail") && product.retail_price) {
      return product.retail_price;
    }
    
    if (isAuthenticated && (userType === "wholesale_dealer" || userType === "wholesale") && product.wholesale_price) {
      return product.wholesale_price;
    }
    
    return product.normal_price;
  };
  
  // Function to add product to cart
  const addToCart = (product) => {
    setCart(prevCart => {
      // Check if product already in cart
      const existingProduct = prevCart.find(item => item.id === product.id);
      
      if (existingProduct) {
        // Increment quantity if already in cart
        return prevCart.map(item => 
          item.id === product.id 
            ? {...item, cartQuantity: item.cartQuantity + 1} 
            : item
        );
      } else {
        // Add new product to cart with quantity 1
        return [...prevCart, {...product, cartQuantity: 1}];
      }
    });
  };
  
  // Function to remove product from cart
  const removeFromCart = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };
  
  // Function to update cart item quantity
  const updateCartQuantity = (productId, newQuantity) => {
    setCart(prevCart => 
      prevCart.map(item => 
        item.id === productId 
          ? {...item, cartQuantity: newQuantity} 
          : item
      ).filter(item => item.cartQuantity > 0)
    );
  };
  
  // Filter products based on the search term
  const filteredProducts = (Array.isArray(products) ? products : []).filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );
  
  return (
    <>
      <div>
        <Navbar />
      </div>
      <div>
        <div className="headline">
          <div className="headline-left">See our products</div>
          <div className="headline-right">
            <div className="right-items">
              <input
                type="text"
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <button className="search-btn">
                <img src="/Images/search_icon.png" alt="Search Icon" />
                Search
              </button>
              {/* Cart Summary */}
              <div className="cart-summary">
                Cart: {cart.reduce((total, item) => total + item.cartQuantity, 0)} items
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* Product List Section */}
      <div className="product-grid">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.id} className="product-card">
              <img 
                src={product.image} 
                alt={product.name} 
                className="product-image" 
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = "/Images/placeholder.png"; // Fallback image
                }}
              />
              <h2 className="product-name">{product.name}</h2>
              <p className="product-category">Category: {product.category}</p>
              <p className="product-price">
                Price: â‚¹{getDisplayPrice(product)}
              </p>
              <div className="product-actions">
                <button 
                  className="add-to-cart-btn"
                  onClick={() => addToCart(product)}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>No products found.</p>
        )}
      </div>
    </>
  );
}

export default Products;
