import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../AuthenticationContext";
import "./products.css";
import Navbar from "../../components/Navbar";

function Products() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();
  const { isAuthenticated, userType } = useAuth();

  // Fetch products from the Django backend
  useEffect(() => {
    fetch("/api/products/", {
      headers: {
        'Authorization': isAuthenticated ? `Token ${localStorage.getItem('authToken')}` : '',
      }
    })
      .then((response) => response.json())
      .then((data) => {
        const productList = Array.isArray(data) ? data : (data.products || []);
        setProducts(productList);
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, [isAuthenticated]);

  // Get correct price based on user type
  const getDisplayPrice = (product) => {
    if (isAuthenticated && userType === "retail_seller" && product.retail_price) return product.retail_price;
    if (isAuthenticated && userType === "wholesale_dealer" && product.wholesale_price) return product.wholesale_price;
    return product.normal_price;
  };

  // Check if user can add to cart
  const canAddToCart = () => isAuthenticated && (userType === "retail_seller" || userType === "wholesale_dealer");

  // Add item to cart or increase quantity
  const addToCart = (product) => {
    setCart(prevCart => {
      const itemExists = prevCart.find(item => item.id === product.id);
      if (itemExists) {
        return prevCart.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...product, quantity: 1, price: getDisplayPrice(product) }];
    });
  };

  // Update quantity (increment or decrement)
  const updateQuantity = (productId, type) => {
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === productId
          ? { ...item, quantity: type === "increase" ? item.quantity + 1 : Math.max(1, item.quantity - 1) }
          : item
      )
    );
  };

  // Navigate to Cart Page
  const goToCart = () => navigate("/cart", { state: { cart } });

  return (
    <>
      <Navbar />
      <div className="headline">
        <div className="headline-left">See our products</div>
        <div className="headline-right">
          <div className="right-items">
            <input type="text" placeholder="Search products..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
            <button className="search-btn">Search</button>
            {cart.length > 0 && <button className="cart-btn" onClick={goToCart}>View Cart ({cart.length})</button>}
          </div>
        </div>
      </div>

      {/* Product List Section */}
      <div className="product-grid">
        {products.length > 0 ? (
          products.filter((product) => product.name.toLowerCase().includes(searchTerm.toLowerCase())).map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.image || "/Images/placeholder.png"} alt={product.name} className="product-image" />
              <h2 className="product-name">{product.name}</h2>
              <p className="product-price">Price: ₹{getDisplayPrice(product)}</p>

              {canAddToCart() && (
                <div className="product-cart-actions">
                  <button className="add-to-cart-btn" onClick={() => addToCart(product)}>ADD TO CART</button>
                </div>
              )}
            </div>
          ))
        ) : (
          <p>No products found.</p>
        )}
      </div>
    </>
  );
}

export default Products;
