import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../../components/Navbar';
import { FormDataContext } from './FormDataContext'; // Import the context
import './signup.css'; // Import the CSS

function Signup() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');
  const userTypes = ['Retail seller', 'Wholesale user'];
  const navigate = useNavigate();
  const { setFormData } = useContext(FormDataContext); // Use the context

  // ✅ Corrected handleChange function
  const handleChange = (event) => {
    setUserType(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    const formDataToSend = {
      username: username,
      email: email,
      password: password,
      user_type: userType
    };
  
    try {
      const response = await fetch("/api/signup/", { 
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formDataToSend),
      });

      
  
      const data = await response.json();
      if (response.ok) {
        alert("Signup successful! Redirecting to registration form...");
        navigate("/rtreg"); 
      } else {
        alert(data.error || "Signup failed. Try again.");
      }
    } catch (error) {
      console.error("Error during signup:", error);
      alert("An error occurred. Please try again later.");
    }
  };
  
  

  return (
    <>
      <div>
        <Navbar />
      </div>
      <div className='signup_container'>
        <div className='image_container'>
          <img src='/Images/side-image_(1).jpg' alt='Decorative' />
        </div>
        <div className='signup_box'>
          <div className='signup_form'>
            <div className='text'>Sign Up</div>
            <form onSubmit={handleSubmit}>
              <div className='input_elements'>
                <div className='input_items'>
                  <img src='/Images/user_icon.png' alt='User Icon' />
                  <input
                    type='text'
                    placeholder='Username'
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                  />
                </div>
                <div className='input_items'>
                  <img src='/Images/email_icon.png' alt='Email Icon' />
                  <input
                    type='email'
                    placeholder='Email'
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <div className='input_items'>
                  <img src='/Images/pwd_icon.png' alt='Password Icon' />
                  <input
                    type='password'
                    placeholder='Password'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                </div>
                <div className='input_items'>
                  <img src='/Images/user_type.png' alt='User Type Icon' />
                  <input 
                    type='text'
                    placeholder='User Type'
                    value={userType}
                    onChange={handleChange}  // ✅ Now it correctly updates userType
                    list="user-types"
                  />
                  <datalist id="user-types">
                    {userTypes.map((type, index) => (
                      <option key={index} value={type} />
                    ))}
                  </datalist>
                </div>
              </div>
              <div className='submit-container'>
                <button type="submit" className='submit'>Signup</button>
              </div>
            </form>
            <div className='links'>
              <a href='/login'>Already have an account? Login</a>
              <br />
              <a href='/forgot-password'>Forgot Password?</a>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Signup;
