import React, { useState } from 'react';
import { Link } from 'react-router-dom';  // Import Link from react-router-dom
import './Navbar.css';

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-transparent position-absolute w-100">
        <div className="container-fluid">
          <a className="navbar-brand" href="/">
            <img src="/Images/logo.png" alt="Logo" width="100" height="50" className="d-inline-block align-text-top" />
          </a>
          <button
            className="navbar-toggler"
            type="button"
            onClick={toggleSidebar} // Toggle sidebar
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-end">
            <ul className="navbar-nav d-none d-lg-flex">
              {/* Replace <a> with <Link> */}
              <li className="nav-item">
                <Link className="nav-link" to="/">Home</Link> {/* Use Link for routing */}
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/products">Products</Link> {/* Use Link for routing */}
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/about_us">About Us</Link> {/* Use Link for routing */}
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/login">Login</Link> {/* Use Link for routing */}
              </li>
            </ul>
          </div>
        </div>
      </nav>

      {/* Sidebar */}
      <div className={`sidebar ${isOpen ? 'open' : ''}`}>
        <button className="close-btn" onClick={toggleSidebar}>&times;</button>
        <ul>
          {/* Replace <a> with <Link> */}
          <li><Link to="/">Home</Link></li> {/* Use Link for routing */}
          <li><Link to="/products">Products</Link></li> {/* Use Link for routing */}
          <li><Link to="/about_us">About Us</Link></li> {/* Use Link for routing */}
          <li><Link to="/login">Login</Link></li> {/* Use Link for routing */}
        </ul>
      </div>

      {/* Optional Backdrop */}
      {isOpen && <div className="backdrop" onClick={toggleSidebar}></div>}
    </>
  );
}

export default Navbar;
