import React, { useState, useEffect } from "react";
import { useAuth } from "../../AuthenticationContext";
import "./products.css";
import Navbar from "../../components/Navbar";

function Products() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const { isAuthenticated, userType } = useAuth();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch products from the Django backend
  useEffect(() => {
    setLoading(true);
    
    // Log the current auth state for debugging
    console.log("Auth state:", { isAuthenticated, userType });
    
    // Get the token from localStorage
    const token = localStorage.getItem('authToken');
    
    fetch("/api/products/", {
      method: "GET",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': token ? `Token ${token}` : '',
      },
      credentials: 'include',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Product data received:", data);
        if (Array.isArray(data.products)) {
          setProducts(data.products);
        } else if (Array.isArray(data)) {
          setProducts(data);
        } else {
          console.error("Fetched data is not in expected format:", data);
          setProducts([]);
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching products:", error);
        setError(error.message);
        setLoading(false);
      });
  }, [isAuthenticated, userType]);

  // Filter products based on the search term
  const filteredProducts = (Array.isArray(products) ? products : []).filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Function to determine which price to display based on user type
  const getDisplayPrice = (product) => {
    // Log the product details to see what fields are available
    console.log("Product details:", product);
    
    if (!isAuthenticated) {
      return product.normal_price || product.price || 0;
    }
    
    // Get current userType from localStorage as a fallback
    const storedUserType = localStorage.getItem('userType');
    const effectiveUserType = userType || storedUserType;
    
    console.log("Effective user type:", effectiveUserType);
    
    switch(effectiveUserType) {
      case 'retail_seller':
        // Return retail price if available, otherwise fallback to normal price
        return product.retail_price || product.normal_price || product.price || 0;
      case 'wholesale_dealer':
        // Return wholesale price if available, otherwise fallback to normal price
        return product.wholesale_price || product.normal_price || product.price || 0;
      default:
        return product.normal_price || product.price || 0;
    }
  };

  // Function to get the correct price label
  const getPriceLabel = () => {
    // Get current userType from localStorage as a fallback
    const storedUserType = localStorage.getItem('userType');
    const effectiveUserType = userType || storedUserType;
    
    if (!isAuthenticated || !effectiveUserType) {
      return "Regular Price";
    }
    
    switch(effectiveUserType) {
      case 'retail_seller':
        return "Retail Price";
      case 'wholesale_dealer':
        return "Wholesale Price";
      default:
        return "Regular Price";
    }
  };

  return (
    <>
      <div>
        <Navbar />
      </div>
      <div>
        <div className="headline">
          <div className="headline-left">See our products</div>
          <div className="headline-right">
            <div className="right-items">
              <input
                type="text"
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <button className="search-btn">
                <img src="/Images/search_icon.png" alt="Search Icon" />
                Search
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Authentication status indicator (for debugging) */}
      <div style={{ padding: "10px", textAlign: "center" }}>
        {isAuthenticated ? (
          <p>Logged in as: {userType || localStorage.getItem('userType') || 'Unknown'}</p>
        ) : (
          <p>Not logged in</p>
        )}
      </div>

      {/* Product List Section */}
      {loading ? (
        <div className="loading">Loading products...</div>
      ) : error ? (
        <div className="error">Error: {error}</div>
      ) : (
        <div className="product-grid">
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <div key={product.id} className="product-card">
                <img src={product.image} alt={product.name} className="product-image" />

                <h2 className="product-name">{product.name}</h2>
                <p className="product-category">Category: {product.category}</p>
                
                {/* Display price based on user type */}
                <p className="product-price">
                  <strong>{getPriceLabel()}: </strong>
                  â‚¹{getDisplayPrice(product)}
                </p>
              </div>
            ))
          ) : (
            <p>No products found.</p>
          )}
        </div>
      )}
    </>
  );
}

export default Products;
