import React, { useState, useEffect } from "react";
import { useAuth } from "../../AuthenticationContext";
import "./products.css";
import Navbar from "../../components/Navbar";

function Products() {
  const [products, setProducts] = useState([]); // Ensure it's an array
  const [searchTerm, setSearchTerm] = useState("");
  const { isAuthenticated, userType } = useAuth();

  useEffect(() => {
    fetch("/api/products/", {
      headers: {
        "Authorization": isAuthenticated ? `Token ${localStorage.getItem("authToken")}` : "",
      },
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Fetched products:", data); // Debugging

        // ✅ Ensure `data` is an array
        if (Array.isArray(data)) {
          setProducts(data);
        } else if (Array.isArray(data.products)) {
          setProducts(data.products);
        } else {
          console.error("Unexpected response format:", data);
          setProducts([]); // Fallback to an empty array
        }
      })
      .catch((error) => {
        console.error("Error fetching products:", error);
        setProducts([]); // Prevents `.map()` from breaking
      });
  }, [isAuthenticated]);

  const filteredProducts = (Array.isArray(products) ? products : []).filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getDisplayPrice = (product) => {
    if (!isAuthenticated) {
      return product.normal_price;
    }

    switch (userType) {
      case "retail_seller":
        return product.retail_price || product.normal_price;
      case "wholesale_dealer":
        return product.wholesale_price || product.normal_price;
      default:
        return product.normal_price;
    }
  };

  return (
    <>
      <Navbar />
      <div>
        <div className="headline">
          <div className="headline-left">See our products</div>
          <div className="headline-right">
            <div className="right-items">
              <input
                type="text"
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <button className="search-btn">
                <img src="/Images/search_icon.png" alt="Search Icon" />
                Search
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Product List Section */}
      <div className="product-grid">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.image} alt={product.name} className="product-image" />
              <h2 className="product-name">{product.name}</h2>
              <p className="product-category">Category: {product.category}</p>

              <p className="product-price">
                Price: ₹{getDisplayPrice(product)}
                {userType && (
                  <span className="user-price-tag">
                    ({userType === "retail_seller" ? "Retail" : "Wholesale"} Price)
                  </span>
                )}
              </p>
            </div>
          ))
        ) : (
          <p>No products found.</p>
        )}
      </div>
    </>
  );
}

export default Products;
