import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext"; // Ensure correct path
import { useNavigate } from "react-router-dom";

const Products = () => {
  const { isAuthenticated, userType } = useAuth();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch products from backend
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/api/products/");
        if (!response.ok) {
          throw new Error("Failed to fetch products");
        }
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error("Error fetching products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // Function to determine price based on user type
  const getDisplayPrice = (product) => {
    if (!isAuthenticated) {
      return { price: product.normal_price, label: "Normal Price" };
    }
    switch (userType) {
      case "retail_seller":
        return { price: product.retail_price ?? product.normal_price, label: "Retail Price" };
      case "wholesale_dealer":
        return { price: product.wholesale_price ?? product.normal_price, label: "Wholesale Price" };
      default:
        return { price: product.normal_price, label: "Normal Price" };
    }
  };

  // Handle Add to Cart (Modify as needed)
  const handleAddToCart = (product) => {
    if (!isAuthenticated) {
      navigate("/login");
      return;
    }
    alert(`Added ${product.name} to cart!`);
  };

  if (loading) {
    return <p>Loading products...</p>;
  }

  return (
    <div className="products-container">
      <h2>Our Products</h2>
      <div className="product-list">
        {products.map((product) => {
          const { price, label } = getDisplayPrice(product);
          return (
            <div key={product.id} className="product-card">
              <img src={product.image_url} alt={product.name} className="product-image" />
              <h3 className="product-name">{product.name}</h3>
              <p className="product-price">
                Price: â‚¹{price} <span className="user-price-tag">({label})</span>
              </p>
              <button className="add-to-cart" onClick={() => handleAddToCart(product)}>
                Add to Cart
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Products;
