import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../../components/Navbar';
import './signup.css';
import { FormDataContext } from './FormDataContext'; // Import the context

function Signup() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [userType, setUserType] = useState('');
  const userTypes = ['Retail seller', 'Wholesale user', 'Staff'];
  const navigate = useNavigate();
  const { setFormData } = useContext(FormDataContext); // Use the context

  const handleChange = (event) => {
    setUserType(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!username || !email || !password || !userType) {
      alert("Please fill in all fields.");
      return;
    }

    // Store username and email in the context
    setFormData({ username: username, email: email });

    // Redirect to the Retailer Registration form
    navigate('/rtreg');
  };

  return (
    <>
    <div></div>
      {/* ... (rest of the component) ... */}
          <form onSubmit={handleSubmit}>
                     <div className='input_elements'>
                       <div className='input_items'>
                         <img src='\Images\user_icon.png' alt='User Icon' />
                         <input
                           type='text'
                           placeholder='Username'
                           value={username}
                           onChange={(e) => setUsername(e.target.value)}
                           required // Add the required attribute
                         />
                       </div>
                       <div className='input_items'>
                         <img src='\Images\email_icon.png' alt='Email Icon' />
                         <input
                           type='email'
                           placeholder='Email'
                           value={email}
                           onChange={(e) => setEmail(e.target.value)}
                           required // Add the required attribute
                         />
                       </div>
                       <div className='input_items'>
                         <img src='\Images\pwd_icon.png' alt='Password Icon' />
                         <input
                           type='password'
                           placeholder='Password'
                           value={password}
                           onChange={(e) => setPassword(e.target.value)}
                           required // Add the required attribute
                         />
                       </div>
                       <div className='input_items'>
                         <img src='\Images\user_type.png' alt='User Type Icon' />
                         <input
                           type='text'
                           placeholder='User Type'
                           value={userType}
                           onChange={handleChange}
                           list="user-types"
                           required // Add the required attribute
                         />
                         <datalist id="user-types">
                           {userTypes.map((type, index) => (
                             <option key={index} value={type} />
                           ))}
                         </datalist>
                       </div>
                     </div>
                     <div className='submit-container'>
                       <button type="submit" className='submit'>Signup</button> {/* Changed to type="submit" */}
                     </div>
                   </form>
      {/* ... (rest of the component) ... */}
    </>
  );
}

export default Signup;
