import React, { useEffect, useState } from "react";
import { useAuth } from "../../AuthenticationContext";
import { useNavigate } from "react-router-dom";

const Products = () => {
  const { isAuthenticated, userType } = useAuth();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]); // Ensure it's always an array
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("/api/products/");
        if (!response.ok) {
          throw new Error("Failed to fetch products");
        }
        const data = await response.json();

        if (Array.isArray(data)) {
          setProducts(data); // ✅ Set only if it's an array
        } else {
          console.error("Unexpected response format:", data);
          setProducts([]); // ✅ Prevents map() error
        }
      } catch (error) {
        console.error("Error fetching products:", error);
        setProducts([]); // ✅ Ensures it's always an array
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  // Handle empty product list case
  if (loading) return <p>Loading products...</p>;
  if (products.length === 0) return <p>No products available.</p>;

  return (
    <div className="products-container">
      <h2>Our Products</h2>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <h3>{product.name}</h3>
            <p>Price: ₹{product.normal_price}</p>
            <button onClick={() => alert(`Added ${product.name} to cart!`)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
