import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './rt_cart.css';
import Navbar from '../../../components/Navbar';
import RetailSidebar from '../../../components/RetailSidebar';

function RtCart() {
  const [cart, setCart] = useState([
    // Example cart items would come from a state management solution or passed as prop
    { id: 1, name: 'Product 1', price: 100, cartQuantity: 2 },
    { id: 2, name: 'Product 2', price: 200, cartQuantity: 1 }
  ]);
  const navigate = useNavigate();

  // Calculate total price
  const totalPrice = cart.reduce((total, item) => total + (item.price * item.cartQuantity), 0);

  // Update cart item quantity
  const updateQuantity = (productId, newQuantity) => {
    setCart(prevCart => 
      prevCart.map(item => 
        item.id === productId 
          ? {...item, cartQuantity: newQuantity} 
          : item
      ).filter(item => item.cartQuantity > 0)
    );
  };

  // Remove item from cart
  const removeItem = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  // Proceed to checkout
  const proceedToCheckout = () => {
    navigate('/checkout', { state: { cartItems: cart } });
  };

  return (
    <>
    <section className="cart-section">
      <div className="nav-section">
        <Navbar/>
      </div>
      <div className="cart-content-section">
        <div className="sidebar-section">
          <RetailSidebar/>
        </div>
        <div className="cart-contents">
          <h1>Your Cart</h1>
          {cart.length === 0 ? (
            <p>Your cart is empty</p>
          ) : (
            <>
              {cart.map(item => (
                <div key={item.id} className="cart-item">
                  <span>{item.name}</span>
                  <div className="quantity-control">
                    <button onClick={() => updateQuantity(item.id, item.cartQuantity - 1)}>-</button>
                    <span>{item.cartQuantity}</span>
                    <button onClick={() => updateQuantity(item.id, item.cartQuantity + 1)}>+</button>
                  </div>
                  <span>â‚¹{item.price * item.cartQuantity}</span>
                  <button onClick={() => removeItem(item.id)}>Remove</button>
                </div>
              ))}
              <div className="cart-summary">
                <h2>Total: â‚¹{totalPrice}</h2>
                <button onClick={proceedToCheckout}>Proceed to Checkout</button>
              </div>
            </>
          )}
        </div>
      </div>
    </section>
    </>
  );
}

export default RtCart;
