import React, { useState, useEffect } from "react";
import "./products.css";
import Navbar from "../../components/Navbar";

function Products() {
  const [products, setProducts] = useState([]); // State to hold product data
  const [searchTerm, setSearchTerm] = useState(""); // State for the search input

  // Fetch products from the Django backend
  useEffect(() => {
    fetch("/api/products/") // Use full URL for development
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => setProducts(data))
      .catch((error) => console.error("Error fetching products:", error));
  }, []);

  // Filter products based on the search term
  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <>
      <div>
        <Navbar />
      </div>
      <div>
        <div className="headline">
          <div className="headline-left">See our products</div>
          <div className="headline-right">
            <div className="right-items">
              <img src="/Images/search_icon.png" alt="Search Icon" />
              <input
                type="text"
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <button className="search-btn"><FontAwesomeIcon icon="fa-solid fa-magnifying-glass" />Search</button>
            </div>
          </div>
        </div>
      </div>

        {/* Product List Section */}
        <div className="product-grid">
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <div key={product.id} className="product-card">
                <img
                  src={`http://127.0.0.1:8000${product.image}`} // Adjust based on your API response
                  alt={product.name}
                  className="product-image"
                />
                <h2 className="product-name">{product.name}</h2>
                <p className="product-category">Category: {product.category}</p>
                <p className="product-price">Price: â‚¹{product.normal_price}</p>
              </div>
            ))
          ) : (
            <p>No products found.</p>
          )}
        </div>
      
    </>
  );
}

export default Products;
