import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../../AuthenticationContext';
import './rt_cart.css';
import Navbar from '../../../components/Navbar';
import RetailSidebar from '../../../components/RetailSidebar';

function RtCart() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();
  const { isAuthenticated, userType } = useAuth();

  // Retrieve cart from localStorage on component mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCart(JSON.parse(savedCart));
    }
  }, []);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  // Function to get the correct price based on user type
  const getDisplayPrice = (product) => {
    if (isAuthenticated && (userType === "retail_seller" || userType === "retail") && product.retail_price) {
      return product.retail_price;
    }
    
    if (isAuthenticated && (userType === "wholesale_dealer" || userType === "wholesale") && product.wholesale_price) {
      return product.wholesale_price;
    }
    
    return product.normal_price;
  };

  // Calculate total price
  const totalPrice = cart.reduce((total, item) => total + (getDisplayPrice(item) * item.quantity), 0);

  // Update cart item quantity
  const updateQuantity = (productId, newQuantity) => {
    setCart(prevCart => 
      prevCart.map(item => 
        item.id === productId 
          ? {...item, quantity: newQuantity} 
          : item
      ).filter(item => item.quantity > 0)
    );
  };

  // Remove item from cart
  const removeItem = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  // Proceed to checkout
  const proceedToCheckout = () => {
    navigate('/checkout', { state: { cartItems: cart } });
  };

  return (
    <>
    <section className="cart-section">
      <div className="nav-section">
        <Navbar/>
      </div>
      <div className="cart-content-section">
        <div className="sidebar-section">
          <RetailSidebar/>
        </div>
        <div className="cart-contents">
          <h1>Your Cart</h1>
          {cart.length === 0 ? (
            <p>Your cart is empty</p>
          ) : (
            <>
              {cart.map(item => (
                <div key={item.id} className="cart-item">
                  <img 
                    src={item.image} 
                    alt={item.name} 
                    className="cart-item-image"
                  />
                  <div className="cart-item-details">
                    <h2>{item.name}</h2>
                    <p>Category: {item.category}</p>
                    <p>Price: ₹{getDisplayPrice(item)}</p>
                  </div>
                  <div className="cart-item-quantity">
                    <div className="quantity-control">
                      <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
                      <span>{item.quantity}</span>
                      <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
                    </div>
                    <p>Subtotal: ₹{getDisplayPrice(item) * item.quantity}</p>
                  </div>
                  <button 
                    className="remove-item-btn"
                    onClick={() => removeItem(item.id)}
                  >
                    Remove
                  </button>
                </div>
              ))}
              <div className="cart-summary">
                <h2>Total: ₹{totalPrice.toFixed(2)}</h2>
                <button 
                  onClick={proceedToCheckout}
                  className="proceed-to-checkout-btn"
                >
                  Proceed to Checkout
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </section>
    </>
  );
}

export default RtCart;
