import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './rtform.css';
import Navbar from '../../components/Navbar';

function RtReg() {
  const location = useLocation();
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [shopName, setShopName] = useState('');
  const [ownerName, setOwnerName] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [locationLink, setLocationLink] = useState('');

  // Extract username and email from URL parameters on component mount
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const usernameFromParams = params.get('username');
    const emailFromParams = params.get('email');

    if (usernameFromParams) {
      setUsername(usernameFromParams);
    }
    if (emailFromParams) {
      setEmail(emailFromParams);
    }
  }, [location.search]); // Dependency array ensures this runs only when location.search changes

  // Function to fetch the user's location
  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          // Generate Google Maps link
          const mapLink = `https://www.google.com/maps?q=${latitude},${longitude}`;
          setLocationLink(mapLink);
        },
        (error) => {
          console.error("Error getting location:", error);
          alert("Unable to retrieve your location.");
        }
      );
    } else {
      alert("Geolocation is not supported by your browser.");
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Prepare the data to send to the Django backend
    const formData = {
      username: username,
      email: email,
      shop_name: shopName,
      owner_name: ownerName,
      mobile_number: mobileNumber,
      location_link: locationLink,
    };

    try {
      const response = await fetch('/api/register-retailer/', {  // Replace with your Django endpoint
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // You might need to include a CSRF token here
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Registration successful!  Awaiting admin verification.');
        // Optionally, redirect the user to a "thank you" page or login page
      } else {
        const errorData = await response.json();
        console.error('Registration failed:', errorData);
        alert('Registration failed. Please try again.');
      }
    } catch (error) {
      console.error('Error during registration:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <>
      <div>
        <Navbar />
      </div>
      <div className='rtreg-signup_container'>
        {/* ... (Image Container - unchanged) ... */}
        <div className='rtreg-signup_box'>
          <div className='rtreg-signup_form'>
            <div className='text'>Retailer Registration</div> {/* Changed text */}
            <form onSubmit={handleSubmit}> {/* Wrap form elements in a <form> tag */}
              <div className='rtreg-input_elements'>
                <div className='rtreg-input_items'>
                  <img src='\Images\user_icon.png' alt='User Icon' />
                  <input type='text' placeholder='Username' value={username} readOnly />
                </div>
                <div className='rtreg-input_items'>
                  <img src='\Images\email_icon.png' alt='Email Icon' />
                  <input type='email' placeholder='Email' value={email} readOnly />
                </div>
                <div className='rtreg-input_items'>
                  <img src='\Images\email_icon.png' alt='Email Icon' />
                  <input
                    type='text'
                    placeholder='Shop name'
                    value={shopName}
                    onChange={(e) => setShopName(e.target.value)}
                    required // Add the required attribute
                  />
                </div>
                <div className='rtreg-input_items'>
                  <img src='\Images\email_icon.png' alt='Email Icon' />
                  <input
                    type='text'
                    placeholder='Owner name'
                    value={ownerName}
                    onChange={(e) => setOwnerName(e.target.value)}
                    required // Add the required attribute
                  />
                </div>
                <div className='rtreg-input_items'>
                  <img src='\Images\email_icon.png' alt='Email Icon' />
                  <input
                    type='number'
                    placeholder='Mobile number'
                    value={mobileNumber}
                    onChange={(e) => setMobileNumber(e.target.value)}
                    required // Add the required attribute
                  />
                </div>
                {/* Display the location link if available */}
                <div className='rtreg-input_items'>
                  <button type="button" onClick={getLocation}>Get Location</button> {/* Button to trigger getLocation */}
                  {locationLink && (
                    <div>
                      <input type='text' value={locationLink} readOnly placeholder='Location Link' />
                      <a href={locationLink} target="_blank" rel="noopener noreferrer">Open in Google Maps</a>
                    </div>
                  )}
                </div>
              </div>
              <div className='submit-container'>
                <button type="submit" className='submit'>Register</button> {/* Changed to type="submit" */}
              </div>
            </form>
            <div className='links'>
              <a href='/login'>Already have an account? Login</a>
              <br />
              <a href='/forgot-password'>Forgot Password?</a>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default RtReg;
