import React, { createContext, useState, useEffect, useContext } from 'react';

// Create the authentication context
const AuthContext = createContext(null);

// Custom hook to use auth context
export const useAuth = () => useContext(AuthContext);

// Auth provider component
export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userType, setUserType] = useState(null);
  const [loading, setLoading] = useState(true);

  // Check auth status on component mount
  useEffect(() => {
    checkAuthStatus();
  }, []);

  // Function to check if user is authenticated
  const checkAuthStatus = async () => {
    try {
      // Check if token exists in localStorage
      const token = localStorage.getItem('authToken');
      const storedUserType = localStorage.getItem('userType');
      
      if (token) {
        // If we have a token, verify it with the backend to get the current user type
        try {
          const response = await fetch('/api/user-profile/', {
            headers: {
              'Authorization': `Token ${token}`
            }
          });
          
          if (response.ok) {
            const data = await response.json();
            // Update userType based on the backend response
            // This ensures frontend and backend user types stay in sync
            setUserType(data.user_type || storedUserType);
            setIsAuthenticated(true);
          } else {
            // Token invalid, clear storage
            logout();
          }
        } catch (error) {
          console.error('Token verification failed:', error);
          // Fallback to stored user type if API call fails
          setUserType(storedUserType);
          setIsAuthenticated(!!storedUserType);
        }
      }
    } catch (error) {
      console.error('Auth check failed:', error);
    } finally {
      setLoading(false);
    }
  };

  // Login function
  const login = (token, type) => {
    localStorage.setItem('authToken', token);
    localStorage.setItem('userType', type);
    setUserType(type);
    setIsAuthenticated(true);
  };

  // Logout function
  const logout = async () => {
    try {
      const token = localStorage.getItem('authToken');
      
      // Call logout endpoint if token exists
      if (token) {
        try {
          await fetch('/api/logout/', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Token ${token}`
            }
          });
        } catch (error) {
          console.error('Logout API call failed:', error);
          // Continue with local logout even if API call fails
        }
      }
      
      // Clear local storage
      localStorage.removeItem('authToken');
      localStorage.removeItem('userType');
      
      // Update state
      setIsAuthenticated(false);
      setUserType(null);
      
      return true;
    } catch (error) {
      console.error('Logout failed:', error);
      return false;
    }
  };

  // Context value
  const value = {
    isAuthenticated,
    userType,
    loading,
    login,
    logout,
    checkAuthStatus
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
