import React, { useState, useEffect } from "react";
import { useAuth } from "../../AuthenticationContext";
import "./products.css"; // Correct CSS file
import Navbar from "../../components/Navbar";

function Products() {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState({});
  const { isAuthenticated, userType } = useAuth();

  // Fetch products from the Django backend
  useEffect(() => {
    fetch("/api/products/", {
      headers: {
        Authorization: isAuthenticated ? `Token ${localStorage.getItem("authToken")}` : "",
      },
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        const productList = Array.isArray(data) ? data : data.products || [];
        setProducts(productList);
      })
      .catch((error) => console.error("Error fetching products:", error));
  }, [isAuthenticated]);

  // Function to get the correct price based on user type
  const getDisplayPrice = (product) => {
    if (isAuthenticated && (userType === "retail_seller" || userType === "retail") && product.retail_price) {
      return product.retail_price;
    }
    if (isAuthenticated && (userType === "wholesale_dealer" || userType === "wholesale") && product.wholesale_price) {
      return product.wholesale_price;
    }
    return product.normal_price;
  };

  // Function to check if user can add to cart
  const canAddToCart = () => {
    return isAuthenticated && (userType === "retail_seller" || userType === "retail" || userType === "wholesale_dealer" || userType === "wholesale");
  };

  // Handle adding products to cart
  const handleAddToCart = (product) => {
    setCart((prevCart) => ({
      ...prevCart,
      [product.id]: prevCart[product.id] ? prevCart[product.id] + 1 : 1,
    }));
  };

  // Handle increasing product quantity
  const handleIncreaseQuantity = (productId) => {
    setCart((prevCart) => ({
      ...prevCart,
      [productId]: prevCart[productId] + 1,
    }));
  };

  // Handle decreasing product quantity
  const handleDecreaseQuantity = (productId) => {
    setCart((prevCart) => {
      const updatedCart = { ...prevCart };
      if (updatedCart[productId] > 1) {
        updatedCart[productId] -= 1;
      } else {
        delete updatedCart[productId];
      }
      return updatedCart;
    });
  };

  // Filter products based on the search term
  const filteredProducts = products.filter((product) => product.name.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <>
      <Navbar />
      <div className="headline">
        <div className="headline-left">See our products</div>
        <div className="headline-right">
          <div className="right-items">
            <input
              type="text"
              placeholder="Search products..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button className="search-btn">
              <img src="/Images/search_icon.png" alt="Search Icon" />
              Search
            </button>
          </div>
        </div>
      </div>

      {/* Product List Section */}
      <div className="product-grid">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.id} className="product-card">
              <img
                src={product.image}
                alt={product.name}
                className="product-image"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = "/Images/placeholder.png";
                }}
              />
              <h2 className="product-name">{product.name}</h2>
              <p className="product-category">Category: {product.category}</p>
              <p className="product-price">Price: ₹{getDisplayPrice(product)}</p>

              {/* Conditional rendering for Add to Cart */}
              {canAddToCart() && (
                <div className="product-cart-actions">
                  {cart[product.id] ? (
                    <div className="quantity-control">
                      <button className="quantity-btn" onClick={() => handleDecreaseQuantity(product.id)}>
                        -
                      </button>
                      <span className="quantity-display">{cart[product.id]}</span>
                      <button className="quantity-btn" onClick={() => handleIncreaseQuantity(product.id)}>
                        +
                      </button>
                    </div>
                  ) : (
                    <button className="add-to-cart-btn" onClick={() => handleAddToCart(product)}>
                      ADD TO CART
                    </button>
                  )}
                </div>
              )}
            </div>
          ))
        ) : (
          <p>No products found.</p>
        )}
      </div>
    </>
  );
}

export default Products;
