import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './rt_cart.css';
import Navbar from '../../../components/Navbar';
import RetailSidebar from '../../../components/RetailSidebar';

function RtCart() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  // Load cart from localStorage on component mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCart(JSON.parse(savedCart));
    }
  }, []);

  // Save to localStorage whenever cart changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  // Calculate total price
  const totalPrice = cart.reduce((total, item) => total + (item.displayPrice * item.quantity), 0);

  // Update cart item quantity
  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeItem(productId);
      return;
    }
    
    setCart(prevCart => 
      prevCart.map(item => 
        item.id === productId 
          ? {...item, quantity: newQuantity} 
          : item
      )
    );
  };

  // Remove item from cart
  const removeItem = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  // Proceed to checkout
  const proceedToCheckout = () => {
    navigate('/checkout', { state: { cartItems: cart, totalPrice } });
  };

// In RtCart.js
useEffect(() => {
  const fetchCart = () => {
    const savedCart = localStorage.getItem('cart');
    console.log("Cart retrieved from localStorage in RtCart.js (before parsing):", savedCart);

    if (savedCart && savedCart !== "[]") {
      setCart(JSON.parse(savedCart));
    } else {
      console.warn("Cart is empty or not retrieved properly.");
    }
  };

  fetchCart();

  window.addEventListener('storage', fetchCart);

  return () => {
    window.removeEventListener('storage', fetchCart);
  };
}, []);

  
  

  return (
    <>
    <section className="cart-section">
      <div className="nav-section">
        <Navbar/>
      </div>
      <div className="cart-content-section">
        <div className="sidebar-section">
          <RetailSidebar/>
        </div>
        <div className="cart-contents">
          <h1>Your Cart</h1>
          {cart.length === 0 ? (
            <p>Your cart is empty</p>
          ) : (
            <>
              <div className="cart-header">
                <span className="product-name-header">Product</span>
                <span className="quantity-header">Quantity</span>
                <span className="price-header">Price</span>
                <span className="actions-header">Actions</span>
              </div>
              {cart.map(item => (
                <div key={item.id} className="cart-item">
                  <span className="product-name">{item.name}</span>
                  <div className="quantity-control">
                    <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
                  </div>
                  <span className="item-price">₹{(item.displayPrice * item.quantity).toFixed(2)}</span>
                  <button className="remove-btn" onClick={() => removeItem(item.id)}>Remove</button>
                </div>
              ))}
              <div className="cart-summary">
                <h2>Total: ₹{totalPrice.toFixed(2)}</h2>
                <button className="checkout-btn" onClick={proceedToCheckout}>Proceed to Checkout</button>
              </div>
            </>
          )}
        </div>
      </div>
    </section>
    </>
  );
}

export default RtCart;
